伙伴ID = ""    --"|"function 取好友ID(NAME)    local tem = LUA_取返回值(string.format([[				name2 ="%s"			XXX=1			for  nIndex=0,80 do				local ID = DataPool:GetFriend( XXX, nIndex, "ID" );				if  ID  then			local name = DataPool:GetFriend( XXX, nIndex, "NAME")					if name2 == name then					  return ID					end				end			end			return -1		]], NAME), "b")    return temendfunction 取玩家ID(玩家名字)    目标名称, 目标ID, 目标X坐标, 目标Y坐标, 目标血量, 目标距离, 目标类别, 目标归属, 怪物判断, 头顶标注 = 遍历周围物品(1, 玩家名字, 15.5, ID顾虑, 模糊名称, 名称过滤)    if 目标ID ~= -1 then        选中对象(目标ID)    --选中目标        延时(2000)        LUA_Call(string.format([[		setmetatable(_G, {__index = ContexMenu_Env});		ContexMenu_OnEvent("SHOW_CONTEXMENU");		setmetatable(_G, {__index = ContexMenu_Env});send_detail();		]]))        延时(3000)        local tem = LUA_取返回值(string.format([[	local szGuid = SystemSetup:GetPrivateInfo("other","guid");	local szGuidText = ConvertGuidToShow( szGuid )	return szGuidText				]]))        增加信任好友(tem)        延时(1000)        return tostring(tem)    else        屏幕提示("提示,周围找不到玩家")        return -1    endendfunction 增加信任好友(strCode)    LUA_Call(string.format([[	setmetatable(_G, {__index = TrustFriendList_Env});	DataPool : AddTrustFriend("%s");	]], strCode))endfunction 批量增加信任好友(AAA)    t = {}    for w in string.gmatch(AAA, "([^'|']+)") do        table.insert(t, w)    end    for k, v in ipairs(t) do        屏幕提示(k .. ":" .. v)        增加信任好友(v)        延时(1000)    endendfunction 取结拜数量()    local tem = LUA_取返回值(string.format([[		local num =  DataPool:GetBrotherCount()		return num	 ]], "n"))    return tonumber(tem)endfunction 取结拜名称(index)    local tem = LUA_取返回值(string.format([[		local charName = DataPool:GetBrotherInfoByInd(%d - 1, "CharName")		return charName	 ]], index))    return tostring(tem)endfunction 结拜加信任()    local 结拜数量 = 取结拜数量()    屏幕提示("结拜数量:" .. 结拜数量)    延时(2000)    if 结拜数量 > 1 then        for i = 1, 结拜数量 do            local 结拜名称 = 取结拜名称(i)            local id = 取好友ID(结拜名称)            屏幕提示(结拜名称 .. id)            延时(2000)            增加信任好友(id)            延时(2000)        end    endendfunction 队友加信任()    for i = 1, 6 do        local 队友名称, 等级, 门派2, 血量, 地图, X坐标, Y坐标 = 组队_获取队员信息(i)        if 队友名称 then            local id = 取好友ID(队友名称)            屏幕提示(队友名称 .. id)            延时(2000)            增加信任好友(id)        end    endend执行功能("大理回城")延时(60000)队友加信任()结拜加信任()